></table></div> <!-- /container --><script src="https://ajax.googleapis.com/ajax/libs/jquery/1.12.4/jquery.min.js"></script><script>$(function()   {    $(".student").click(function(e) {        e.preventDefault();        $('div.student-details:visible').slideUp();        $(this).next('div.student-details').slideDown();    });});</script></body></html>