<?phpini_set('display_errors',1);// Read JSON file$json = file_get_contents('resources/students.json');//Decode JSON$json_data = json_decode($json, true);function sort_names(){    $args = func_get_args();    $data = array_shift($args);    foreach ($args as $n => $field) {        if (is_string($field)) {            $tmp = array();            foreach ($data as $key => $row)                $tmp[$key] = $row[$field];            $args[$n] = $tmp;            }    }    $args[] = &$data;    call_user_func_array('array_multisort', $args);    return array_pop($args);}$sorted = sort_names($json_data, 'firstName', SORT_ASC, 'lastName', SORT_ASC);function get_count($name){	$counter = 0;	$json = file_get_contents('resources/students.json');	$json_data = json_decode($json, true);		foreach($json_data as $value){		if($value['firstName'] == $name)			$counter++;		}	return $counter;}function get_last($name, $l){	$counter_ln = 0;	$json = file_get_contents('resources/students.json');	$json_data = json_decode($json, true);		foreach($json_data as $value){		$l_check=substr($value['lastName'], 0,1);		if($value['firstName'] == $name && $l_check == $l)			$counter_ln++;		}	return $counter_ln;}?>